# print(util(0, len(s)-1))
# print(time.time() - start_time, " seconds")




dp = [[0 for c in range(len(s))] for r in range(len(s))]
        
for i in range(len(s)-1, -1, -1):
    for j in range(len(s)):
        if i>=j: continue
        if s[i] == s[j]:
            dp[i][j] = dp[i+1][j-1]
        else:
            dp[i][j] = 1 + min(dp[i+1][j], dp[i][j-1])
print(dp[0][-1])
print(time.time() - start_time, " seconds")
